import React, { useState } from 'react';
import { View, TouchableOpacity } from 'react-native';
import { ListItem, Text, Overlay, Input, Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';

import styles from '../stylesLists';

const GroceryList = ({ navigation, route }) => {
  const id = route.params.id;

  const [product, setProduct] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0.0);

  const [visibleCreate, setVisibleCreate] = useState(false);

  const [lists, setLists] = useState([
    {
      id: 123,
      items: [
        {
          id: 111,
          name: 'Manteiga 500g',
          quantity: 1,
          price: 5.89,
        },
        {
          id: 111,
          name: 'Farinha 1Kg',
          quantity: 3,
          price: 4.76,
        },
      ],
    },
    {
      id: 456,
      items: [
        {
          id: 333,
          name: 'Escova de dentes 1un',
          quantity: 1,
          price: 3.98,
        },
        {
          id: 444,
          name: 'Creme dental menta',
          quantity: 4,
          price: 2.78,
        },
        {
          id: 555,
          name: 'Enxaguante bucal',
          quantity: 1,
          price: 9.68,
        },
      ],
    },
  ]);

  const cleanState = () => {
    setProduct('');
    setQuantity(0);
    setPrice(0.0);
  };

  const findList = () => {
    let fetchedList;

    lists.forEach((list) => {
      if (list.id == id) fetchedList = list;
    });

    return fetchedList ? fetchedList : null;
  };

  const toggleOverlayCreate = () => {
    setVisibleCreate(!visibleCreate);
  };

  const handlerCreate = () => {
    const newProduct = {
      id: Math.floor(Math.random() * 9999),
      name: product,
      quantity: parseInt(quantity),
      price: parseFloat(price),
    };

    lists.forEach((list) => {
      if (list.id == id) list.items.push(newProduct);
    });

    setLists(lists);
    cleanState();
    toggleOverlayCreate();
  };

  return (
    <View style={styles.container}>
      {findList()
        ? findList().items.map((item, index) => (
            <ListItem key={index} bottomDivider>
              <ListItem.Content style={styles.listContent}>
                <Text style={styles.quantity}>{item.quantity}</Text>
                <Text style={styles.name}>{item.name}</Text>
              </ListItem.Content>
              <Text>R$ {item.price}</Text>
            </ListItem>
          ))
        : ''}

      <Overlay isVisible={visibleCreate} onBackdropPress={toggleOverlayCreate}>
        <Input
          label="Produto"
          placeholder="Produto"
          value={product}
          onChangeText={(text) => setProduct(text)}
        />
        <Input
          label="Quantidade"
          placeholder="Quantidade"
          value={quantity}
          onChangeText={(text) => setQuantity(text)}
        />
        <Input
          label="Preço"
          placeholder="Preço"
          value={price}
          onChangeText={(text) => setPrice(text)}
        />
        <Button title="Criar" onPress={handlerCreate} />
      </Overlay>

      <TouchableOpacity style={styles.buttonFloat} onPress={toggleOverlayCreate}>
        <Icon name="plus" size={20} color="#f2f2f2" />
      </TouchableOpacity>
    </View>
  );
};

export default GroceryList;
