import React, { useState } from 'react';
import { View } from 'react-native';
import { ListItem, Text, Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';

import styles from '../stylesLists';

const ListOfLists = ({ navigation }) => {
  const [lists, setLists] = useState([
    {
      id: 123,
      title: 'Lista top',
      date: '05/04/2021',
      price: 353.44,
    },
    {
      id: 456,
      title: 'Lista supimpa',
      date: '12/03/2021',
      price: 793.42,
    },
  ]);

  return (
    <View>
      {lists.map((list, index) => (
        <ListItem
          key={index}
          onPress={() => navigation.navigate('GroceryList', { id: list.id })}
          bottomDivider
        >
          <ListItem.Content>
            <ListItem.Title>{list.title}</ListItem.Title>
            <ListItem.Subtitle>{list.date}</ListItem.Subtitle>
          </ListItem.Content>
          <Text>R$ {list.price}</Text>
          <ListItem.Chevron />
        </ListItem>
      ))}

      <Button>
        <Icon name="plus" style={styles.buttonFloat} />
      </Button>
    </View>
  );
};

export default ListOfLists;
