import React, { useState } from 'react';
import { View, TouchableOpacity } from 'react-native';
import { ListItem, Text, Overlay } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';

import styles from '../stylesLists';

const ListOfLists = ({ navigation }) => {
  const [visibleCreate, setVisibleCreate] = useState(false);
  const [lists, setLists] = useState([
    {
      id: 123,
      title: 'Lista top',
      date: '05/04/2021',
      price: 353.44,
    },
    {
      id: 456,
      title: 'Lista supimpa',
      date: '12/03/2021',
      price: 793.42,
    },
  ]);

  return (
    <View style={styles.container}>
      {lists.map((list, index) => (
        <ListItem
          key={index}
          onPress={() => navigation.navigate('GroceryList', { id: list.id })}
          bottomDivider
        >
          <ListItem.Content>
            <ListItem.Title>{list.title}</ListItem.Title>
            <ListItem.Subtitle>{list.date}</ListItem.Subtitle>
          </ListItem.Content>
          <Text>R$ {list.price}</Text>
          <ListItem.Chevron />
        </ListItem>
      ))}

      <TouchableOpacity style={styles.buttonFloat} onPress={() => console.log('add')}>
        <Icon name="plus" size={20} color="#f2f2f2" />
      </TouchableOpacity>

      <Overlay isVisible={visibleCreate} onBackdropPress={toggleOverlayCreate}>
        <Text>Hello from Overlay!</Text>
      </Overlay>
    </View>
  );
};

export default ListOfLists;
