import React from 'react';
import { View, Text } from 'react-native';

const GroceryList = ({ navigation, route }) => {
  const id = route.params.id;

  const lists = [
    {
      id: 123,
      itens: [
        {
          id: 111,
          name: 'Manteiga 500g',
          quantity: 1,
          price: 5.89,
        },
        {
          id: 111,
          name: 'Farinha 1Kg',
          quantity: 3,
          price: 4.76,
        },
      ],
    },
    {
      id: 456,
      itens: [
        {
          id: 333,
          name: 'Escova de dentes 1un',
          quantity: 1,
          price: 3.98,
        },
        {
          id: 444,
          name: 'Creme dental menta',
          quantity: 4,
          price: 2.78,
        },
        {
          id: 555,
          name: 'Enxaguante bucal',
          quantity: 1,
          price: 9.68,
        },
      ],
    },
  ];

  const findList = () => {
    let fetchedList;

    lists.forEach((list) => {
      if (list.id == id) fetchedList = list;
    });

    return fetchedList;
  };

  return (
    <View>
      {findList().itens.map((item, index) => (
        <ListItem
          key={index}
          onPress={() => navigation.navigate('GroceryList', { id: item.id })}
          bottomDivider
        >
          <ListItem.Content>
            <Text>{item.quantity}</Text>
            <ListItem.Title>{item.name}</ListItem.Title>
          </ListItem.Content>
          <Text>R$ {item.price}</Text>
          <ListItem.Chevron />
        </ListItem>
      ))}
    </View>
  );
};

export default GroceryList;
